<template>
  <div>
    <nav-header></nav-header>
    <nav-bread>
      <span slot="bread">Goods</span>
    </nav-bread>
    <div class="accessory-result-page accessory-page">
        <div class="container">
            <div class="filter-nav"><span class="sortby">Sort by:</span> <a href="javascript:void(0)" class="default cur">Default</a>
                <a href="javascript:void(0)" class="price sort-up">Price
                    <svg class="icon icon-arrow-short">
                        <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-arrow-short"></use>
                    </svg>
                </a> <a href="javascript:void(0)" class="filterby stopPop" @click="showFilterPop">Filter by</a></div>
            <div class="accessory-result">
                <div id="filter" class="filter stopPop" v-bind:class="{'filterby-show':filterBy}">
                    <dl class="filter-price">
                        <dt>Price:</dt>
                        <dd><a href="javascript:void(0)" v-bind:class="{cur:priceChecked === 'all'}">All</a></dd>
                        <dd v-for="(price, index) in priceFilter">
                          <a href="javascript:void(0)" @click="setPriceFilter(index)" v-bind:class="{cur:priceChecked===index}">{{price.startPrice}} - {{price.endPrice}}</a>
                        </dd>
                    </dl>
                </div>
                <div class="accessory-list-wrap">
                    <div class="accessory-list col-4">
                        <ul>
                            <li v-for="(item, index) in goodsList">
                                <div class="pic"><a href="#"><img alt="" v-lazy="'/static/'+item.productImg"></a></div>
                                <div class="main">
                                    <div class="name">{{ item.productName }}</div>
                                    <div class="price">{{ item.productPrice }}</div>
                                    <div class="btn-area"><a href="javascript:;" class="btn btn--m">加入购物车</a></div>
                                </div>
                            </li>
                        </ul>
                    </div>
                    <div infinite-scroll-disabled="busy" infinite-scroll-distance="20" class="view-more-normal"><img
                            src="data:image/svg+xml;base64,"
                            style="display: none;"></div>
                </div>
            </div>
        </div>
    </div>
    <div class="md-overlay" @click="closePop" v-show="overLayFlag"></div>
    <nav-footer></nav-footer>
  </div>
</template>

<script>
  import './../assets/css/base.css';
  import './../assets/css/product.css';
  import NavHeader from '@/components/NavHeader.vue';
  import NavBread from '@/components/NavBread.vue'
  import NavFooter from '@/components/NavFooter.vue';
  import axios from 'axios';
  export default {
    name: 'Goodslist',
    data () {
      return {
        goodsList: [],
        priceFilter:[
          {
            startPrice:'0.00',
            endPrice:'500.00'
          },
          {
            startPrice:'500.00',
            endPrice:'1000.00'
          },
          {
            startPrice:'1000.00',
            endPrice:'2000.00'
          }
        ],
        priceChecked:'all',
        filterBy:false,
        overLayFlag:false
      }
    },
    mounted() {
      this.getGoodsList();
    },
    methods:{
      jump(){
        this.$router.push("/carts?id=123");
        // this.$router.go(-2);
      },
      getGoodsList() {
        axios.get("/goods").then((res) => {
          this.goodsList = res.data.result;
        });
      },
      showFilterPop(){
        this.filterBy = true;
        this.overLayFlag = true;
      },
      closePop(){
        this.filterBy = false;
        this.overLayFlag = false;
      },
      setPriceFilter(index){
        this.priceChecked = index;
        this.closePop();
      }
    },
    components:{
      NavHeader,
      NavFooter,
      NavBread
    }
  }
</script>
